import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar/Navbar';
import Player from './components/Player';
import Header from './components/header';
import Formulaire from './components/Pages/Formulaire';
import Music0 from './music/transition_video.mp3';
import Music from './music/Asgeir-King-and-Cross.mp3';
import Music2 from './music/RATM-Killing in the Name.mp3';
import HomePage from './components/Pages/HomePage';
import SuggestionPlaylist from './components/SuggestionPlaylist'
import './font.css';
import { useState } from 'react';

function App() {

  const [savedMood, setSavedMood] = useState("");
  const savingMood = (mood)=> setSavedMood(mood); 
  return (
    <Router>
      <div className="App">
        <Header />

        <Routes>
          <Route exact path="/" element={<HomePage />} />
          <Route path="/form" element={<Formulaire onAnswer={(mood)=>{savingMood(mood)}}/>} />
          <Route path="/suggests" element={<SuggestionPlaylist myMood={savedMood} onAnswered={(answer) => savePlaylist(answer)}/>} />
        </Routes>

        {/* <Player className="player" tracks={[Music0, Music, Music2]} /> */}
        <Navbar />
      </div>
    </Router>
  );
}

export default App;
