import { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';

import './DeezeflowPlaylists.css';
import ColorThemeContext from './Contexts/ColorThemeContext';

function DeezeflowPlaylists({ onPlaylistSelection = () => {} }) {

	const { myColorTheme } = useContext(ColorThemeContext);

	const [ DeezePlaylistSelection, setDeezePlaylistSelection ] = useState([]);

	useEffect(() => {
		fetch('http://localhost:3001/user/4633045002/playlists')
			.then((response) => response.json())
			.then((data) => setDeezePlaylistSelection(data.data));
	}, []);

	return (
		<div className="suggest">
			<h3 className={` ${myColorTheme? `theme-${myColorTheme}-shadow`:"theme-default-shadow"}`}>
				<span>Nos Playlists</span>
			</h3>
			<div className="deezeflow-suggest">
				{DeezePlaylistSelection.slice(0, 7).map((playlist) => (
					<Link to={`/my-playlist/deezeflow/${playlist.id}`}>
						<div
							className="playlistDeezeflow"
							onClick={() => {
								onPlaylistSelection(playlist.id);
							}}
						>
							<img src={playlist.picture_medium} alt="cover" />
						</div>
					</Link>
				))}
			</div>
		</div>
	);
}

export default DeezeflowPlaylists;
