import questionsData from '../Questions/QuestionsData';
import { addAnswer, resetMood, calculateScore } from '../Questions/AlgoMood';
import QuestionsComponent from '../Questions/QuestionsComponent';
import './Formulaire.css';
import { useState, useEffect } from 'react';

const Formulaire = () => {
	const [ step, setStep ] = useState(0);
	const [ getItStarted, letGetItStarted ] = useState(false);
	const [ myMood, setMymood ] = useState('');
	useEffect(
		() => {
			let score = calculateScore();
			setMymood(score);
		},
		[ step === questionsData.length ]
	);

	function nextStep() {
		setStep(step + 1);
	}

	function resetStep() {
		setStep(0);
	}

	const startQuestions = () => {
		letGetItStarted(!getItStarted);
	};

	return (
		<div>
			{!getItStarted ? (
				<div className="welcome-screen">
					<h1>Voyons un peu dans quel mood tu es</h1>
					<h3>Voici quelques questions pour définir ton mood</h3>
					<button
						onClick={() => {
							resetMood();
							startQuestions();
						}}
					>
						Démarrer
					</button>
				</div>
			) : getItStarted && step !== questionsData.length ? (
				<QuestionsComponent
					questions={questionsData[step]}
					onAnswered={(answer) => {
						addAnswer(answer);
						nextStep();
					}}
				/>
			) : (
				getItStarted &&
				step === questionsData.length && (
					<div className="end-screen">
						<h1>Nous avons pu détecter un résultat</h1>
						<p className="mood">
							Apparemment tu es dans un mood de <span>{myMood}</span>
						</p>
						<button
							onClick={() => {
								resetMood();
								startQuestions();
								resetStep();
							}}
						>
							Rejouer
						</button>
						<Link to="/suggests"><button>Voir mes suggestions</button></Link>
					</div>
				)
			)}
		</div>
	);
};

export default Formulaire;
