import { useEffect, useState } from 'react';
import { BrowserRouter, Switch, Link, Route } from 'react-router-dom';
import MyPlaylist from './components/MyPlaylist';
import { myMoodPlaylist, mySearchPlaylist } from './components/PlaylistsData';

const myMood = 'skyrim'; //TO REMOVE !!!
function SuggestionPlaylist({myMood, onAnswered = () => {} }) {
	const [ moodPlaylistSelection, setMoodPlaylistSelection ] = useState([]);
	const [ searchPlaylistSelection, setSearchPlaylistSelection ] = useState([]);
	useEffect(
		() => {
			fetch(myMoodPlaylist.url)
				.then((response) => response.json())
				.then((data) => setMoodPlaylistSelection(data));
		},
		[ myMood ]
	);

	useEffect(
		() => {
			fetch(mySearchPlaylist.url)
				.then((response) => response.json())
				.then((data) => setSearchPlaylistSelection(data.data));
		},
		[ myMood ]
	);

	return (
		<div>
      <h3>Notre suggestion pour ton mood</h3>
			{/* <Link to = {`/my-playlist/deezeflow/${moodPlaylistSelection.id}`}> */}
      <div onClick={onAnswered(moodPlaylistSelection.id)}>
				<p>{moodPlaylistSelection.title}</p>
				<img src={moodPlaylistSelection.picture_medium} alt="cover" />
			</div>
      {/* </Link> */}

			{searchPlaylistSelection.slice(0,5).map((index) => (
				// <Link to = {`/my-playlist/search/${index.id}`}>
        <div onClick={onAnswered(index.id)}>
					<p>{index.title}</p>
					<img src={index.picture_small} alt="cover" />
				</div>
        // </Link>
			))}

		</div>
	);
}

export default SuggestionPlaylist;
