import { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

import Navbar from './components/Navbar/Navbar';
import Player from './components/Player';
import Header from './components/header';
import Formulaire from './components/Pages/Formulaire';
import HomePage from './components/Pages/HomePage';
import SuggestionPlaylist from './components/SuggestionPlaylist';
import DeezeflowPlaylists from './components/DeezeflowPlaylists';
import MyPlaylist from './components/MyPlaylist';

import './font.css';

function App() {
	const [ savedMood, setSavedMood ] = useState('');
	const [ playlistID, setPlaylistId ] = useState('');
	const [ currentTracklist, setCurrentTracklist ] = useState();
	const [ myColorTheme, setMyColorTheme ] = useState();
	return (
		<Router>
			<div className="App">
				<Header myColorTheme={myColorTheme} setMyColorTheme={setMyColorTheme}/>

				<Routes>
					<Route exact path="/" element={<HomePage />} />
					<Route
						path="/form"
						element={
							<Formulaire
								onMoodChange={(mood) => {
									setSavedMood(mood);
									setMyColorTheme(mood);
								}}
							/>
						}
					/>
					<Route
						path="/suggests"
						element={
							<SuggestionPlaylist myMood={savedMood} myColorTheme={myColorTheme} onPlaylistSelection={(id) => setPlaylistId(id)} />
						}
					/>
					<Route
						path="/deezeflow-playlists"
						element={
							<DeezeflowPlaylists  onPlaylistSelection={(id) => setPlaylistId(id)} />
						}
					/>
					<Route
						path="/my-playlist/:source/:id"
						element={
							<MyPlaylist
								myPlaylistId={playlistID}
								onSendingForPlayer={(tracklist) => {
									setCurrentTracklist(tracklist);
								}}
							/>
						}
					/>
				</Routes>

				{currentTracklist && <Player className="player" tracks={currentTracklist} myColorTheme={myColorTheme}/>}

				<Navbar />
			</div>
		</Router>
	);
}

export default App;
