import { useState, useEffect } from 'react';
import './MyPlaylist.css'
const MyPlaylist = ({myPlaylistId, onSendingForPlayer=()=>{}}) => {
  const [myPlaylist, setMyPlaylist] = useState([]);
	const [myPlaylistCover, setMyPlaylistCover] = useState("");
  useEffect(() => {
    fetch(`http://localhost:3001/playlist/${myPlaylistId}`)
      .then((response) => response.json())
      .then((data) => {setMyPlaylist(data.tracks.data);
				setMyPlaylistCover(data["picture_medium"])
			});

  }, []);

  const [playerData, setPlayerData] = useState([]);
  useEffect(() => {
    myPlaylist.map((index) =>
      setPlayerData(
        playerData.push({
          bigPicture: index.album.cover_big,
          smallPicture: index.album.cover_small,
          title: index.title,
          artist: index.artist.name,
          track: index.preview,
        })
      )
    );
  }, [myPlaylist]);
  return (
    <div className="playlist-container">
			<img className="playlist-cover" src={myPlaylistCover} alt = "cover"/>
      <button onClick={()=>{onSendingForPlayer(playerData)}}>PLAY</button>
      <div>
        {myPlaylist.map((index) => (
          <div classeName="track-info-box">
            <img src={index.album.cover_small} alt="cover" /> 
						<div className="track-info-text"><h3>{index.title}</h3>
            <h3>{index.artist.name}</h3></div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyPlaylist;
