import './QuestionComponent.css';
import ColorThemeContext from '../../Contexts/ColorThemeContext';
import { useEffect, useContext } from 'react';

const QuestionsComponent = ({ questions, onAnswered = () => {} }) => {
	const { myColorTheme, setMyColorTheme } = useContext(ColorThemeContext);

	return (
		<div className="question-screen">
			<div className="placeholder" />
			<h3 className="box-questions">{questions.question}</h3>
			<ul>
				{questions.answers.map((resp) => (
					<li
						className="box-answers cursor-pointer"
						onClick={() => {
							onAnswered(resp.mood);
						}}
					>
						{resp.title}
					</li>
				))}
			</ul>
			<div className="placeholder" />
		</div>
	);
};

export default QuestionsComponent;
