import { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

import Navbar from './components/Navbar/Navbar';
import Player from './components/Player';
import Header from './components/header';
import Formulaire from './components/Pages/Formulaire';
import HomePage from './components/Pages/HomePage';
import SuggestionPlaylist from './components/SuggestionPlaylist';
import DeezeflowPlaylists from './components/DeezeflowPlaylists';
import MyPlaylist from './components/MyPlaylist';
import ColorThemeContext from './Contexts/ColorThemeContext';

import './font.css';

function App() {
	const [ savedMood, setSavedMood ] = useState('');
	const [ playlistID, setPlaylistId ] = useState('');
	const [ currentTracklist, setCurrentTracklist ] = useState();
	const [ myColorTheme, setMyColorTheme ] = useState();
	const [ getItStarted, letGetItStarted ] = useState(false);
	const [ step, setStep ] = useState(0);

	const startQuestions = () => {
		letGetItStarted(!getItStarted);
	};

	function resetStep() {
		setStep(0);
	}

	return (
		<ColorThemeContext.Provider value={{myColorTheme:myColorTheme}}>

		<Router>
			<div className="App">
				<Header setMyColorTheme={setMyColorTheme}/>

				<Routes>
					<Route exact path="/" element={<HomePage startQuestions={startQuestions} resetStep={resetStep}/>} />
					<Route
						path="/form"
						element={
							<Formulaire 
							getItStarted={getItStarted} 
							startQuestions={startQuestions}
								onMoodChange={(mood) => {
									setSavedMood(mood);
									setMyColorTheme(mood);
								}
							}
							/>
						}
					/>
					<Route
						path="/suggests"
						element={
							<SuggestionPlaylist myMood={savedMood} onPlaylistSelection={(id) => setPlaylistId(id)} />
						}
					/>
					<Route
						path="/deezeflow-playlists"
						element={
							<DeezeflowPlaylists onPlaylistSelection={(id) => setPlaylistId(id)} />
						}
					/>
					<Route
						path="/my-playlist/:source/:id"
						element={
							<MyPlaylist
								myPlaylistId={playlistID}
								onSendingForPlayer={(tracklist) => {
									setCurrentTracklist(tracklist);
								}}
							/>
						}
					/>
				</Routes>

				{currentTracklist && <Player className="player" tracks={currentTracklist} />}

				<Navbar />
			</div>
		</Router>
		</ColorThemeContext.Provider>
	);
}

export default App;
