import { Link } from 'react-router-dom';
import './HomePage.css';
import StartQuestionnaireContext from './../Contexts/StartQuestionnaire';
import { shuffle } from '../Questions/AlgoQuestion';
import questionsData from '../Questions/QuestionsData';
import { useContext } from 'react';

const HomePage = () => {
  const { resetMood, startQuestions } = useContext(StartQuestionnaireContext);
  return (
    <div>
      <div className="divButton">
        <Link to="/form">
          <button 
          type="button" 
          className="buttonMood"
          onClick={() => {
            resetMood();
            shuffle(questionsData, true);
            startQuestions();
          }}
          >
            {' '}
            <h2> Dans quel mood es-tu?</h2>
            <p>
              {' '}
              <br /> Remplis notre questionnaire pour trouver la playlist
              parfaitement adaptée à ton humeur du moment!
            </p>{' '}
          </button>
        </Link>{' '}
      </div>

      <div>
        <button type="button">
          <Link to="/deezeflow-playlists"> NOS PLAYLISTS </Link>
        </button>
      </div>
    </div>
  );
};

export default HomePage;
