import { useState, useEffect } from 'react';
import './MyPlaylist.css';
const MyPlaylist = ({ myPlaylistId, onSendingForPlayer = () => {} }) => {
	const [ myPlaylist, setMyPlaylist ] = useState([]);
	const [ myPlaylistCover, setMyPlaylistCover ] = useState('');
	useEffect(() => {
		fetch(`http://localhost:3001/playlist/${myPlaylistId}`).then((response) => response.json()).then((data) => {
			setMyPlaylistCover(data['picture_medium']);
			setMyPlaylist(
				data.tracks.data.map((track) => ({
					bigPicture: track.album.cover_big,
					smallPicture: track.album.cover_small,
					title: track.title,
					artist: track.artist.name,
					track: track.preview,
				}))
			)
		});
	}, []);


  return (
<div className="playlist-container">
			<img className="playlist-cover" src={myPlaylistCover} alt="cover" />
			<button
				onClick={() => {
					onSendingForPlayer(myPlaylist);
				}}
			>
				PLAY
			</button>
			<div>
				{myPlaylist.map((track) => (
					<div className="track-info-box">
						<img src={track.smallPicture} alt="cover" />
						<div className="track-info-text">
							<h3>{track.title}</h3>
							<h3>{track.artist}</h3>
						</div>
					</div>
				))}
			</div>
		</div>
  );
};

export default MyPlaylist;
