import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { moodPlaylist, searchPlaylist } from './PlaylistsData';

import './suggest.css';

function SuggestionPlaylist({ myMood, onPlaylistSelection = () => {} }) {
  const [moodPlaylistSelection, setMoodPlaylistSelection] = useState([]);
  const [searchPlaylistSelection, setSearchPlaylistSelection] = useState([]);

  useEffect(() => {
    const myMoodPlaylist = moodPlaylist.find(
      (object) => object.mood === myMood
    );
    fetch(myMoodPlaylist.url)
      .then((response) => response.json())
      .then((data) => setMoodPlaylistSelection(data));
  }, []);

  useEffect(() => {
    const mySearchPlaylist = searchPlaylist.find(
      (object) => object.mood === myMood
    );
    fetch(mySearchPlaylist.url)
      .then((response) => response.json())
      .then((data) => setSearchPlaylistSelection(data.data));
  }, []);

  return (
    <div className="suggest">
      <h3>
        <span>Notre suggestion pour ton mood</span>
      </h3>
      <Link to={`/my-playlist/deezeflow/${moodPlaylistSelection.id}`}>
        <div
          className="playlistMood"
          onClick={() => {
            onPlaylistSelection(moodPlaylistSelection.id);
          }}
        >

          <img src={moodPlaylistSelection.picture_medium} alt="cover" />
        </div>
      </Link>
      <h2>D'autre suggestion</h2>
      <div className="suggestOther">
        <div className="suggestOtherScroll">
          {searchPlaylistSelection.slice(0, 5).map((index) => (
            <Link to={`/my-playlist/search/${index.id}`}>
              <div
                onClick={() => {
                  onPlaylistSelection(index.id);
                }}
              >
                <img src={index.picture_big} alt="cover" />
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}

export default SuggestionPlaylist;
