import { useState, useEffect, useRef } from 'react';

import Slider from 'rc-slider';

import 'rc-slider/assets/index.css';
import '../player.css';

function msToTime(s) {
  let sec = parseInt(s, 10);

  // Pad to 2 or 3 digits, default is 2
  function pad(n, z) {
    const v = z || 2;
    return `00${n}`.slice(-v);
  }

  const secs = sec % 60;
  sec = (sec - secs) / 60;
  const mins = sec % 60;

  return `${pad(mins)}:${pad(secs)}`;
}

function Player({ tracks }) {
  const audioElements = useRef([]);

  const [currentPlayingTrack, setCurrentPlayingTrack] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isAutoPlaying, setIsAutoPlaying] = useState(false);
  const [isEnded, setIsEnded] = useState(false);
  const [isDisplayOff, setIsDisplayOff] = useState(false);
  /**
   * Retourne une référence vers l'élément DOM de la chanson
   * en cours
   */
  function getCurrentAudioElement() {
    return audioElements.current[currentPlayingTrack];
  }

  function pause() {
    getCurrentAudioElement().pause();
    setIsPlaying(false);
  }

  function stop() {
    pause();
    getCurrentAudioElement().currentTime = 0;
  }

  const onTimeUpdate = () => {
    setCurrentTime(getCurrentAudioElement().currentTime);
  };

  /**
   * A la fin de la chanson change la musique
   */
  const onEnded = () => {
    setIsEnded(true);
  };

  function getDuration() {
    return getCurrentAudioElement() ? getCurrentAudioElement().duration : 0;
  }

  function play() {
    getCurrentAudioElement().play();
    setIsPlaying(true);
  }

  function changeTrack(direction) {
    if (
      (direction === -1 && currentPlayingTrack === 0) ||
      (direction === 1 &&
        currentPlayingTrack === audioElements.current.length - 1)
    ) {
      return;
    }

    setIsEnded(false);
    setIsAutoPlaying(isPlaying);
    stop();
    setCurrentPlayingTrack(currentPlayingTrack + direction);
  }

  function onSliderClick(time) {
    setCurrentTime(time);
    getCurrentAudioElement().currentTime = time;
  }

  useEffect(() => {
    getCurrentAudioElement().addEventListener('timeupdate', onTimeUpdate);
    getCurrentAudioElement().addEventListener('ended', onEnded);

    if (isAutoPlaying) {
      play();
    }
  }, [currentPlayingTrack]);

  useEffect(() => {
    if (isEnded) {
      changeTrack(1);
    }
  }, [isEnded]);

  const triggerToggle = () => {
    setIsDisplayOff(!isDisplayOff);
  };

  return (
    <div className={`player ${!isDisplayOff ? 'isDisplayOff-checked' : ''}`}>
      <div className="playerBlock">
        <span onClick={() => triggerToggle()} className="icon-arrow"></span>
        <div>
          <img
            src={tracks[currentPlayingTrack].bigPicture}
            alt="Pochette de l'album"
          />
        </div>
      </div>
      <div className="globalPlayer">
        <div className="titlePlayer">
          <p className="title-track">{tracks[currentPlayingTrack].title}</p>
          <p className="singer">{tracks[currentPlayingTrack].artist}</p>
        </div>
        <Slider
          value={currentTime}
          max={getDuration()}
          onChange={onSliderClick}
        />

        <div className="blockTime">
          <p>{msToTime(currentTime)}</p>
          <p>{msToTime(getDuration())}</p>
        </div>

        <div className="blockPlayer">
          {tracks.map((track, i) => (
            <audio
              ref={(element) => {
                audioElements.current[i] = element;
              }}
            >
              <source src={tracks[currentPlayingTrack].track} />
            </audio>
          ))}

          <div className="buttonPlayer">
            <button
              type="button"
              className="prev"
              aria-label="Previous"
              onClick={() => changeTrack(-1)}
            >
              <div>
                <span class="icon-next"></span>
              </div>
            </button>

            {isPlaying ? (
              <button className="pause" onClick={pause}>
                <div>
                  <span class="icon-pause"></span>
                </div>
              </button>
            ) : (
              <button className="play" onClick={play}>
                <div>
                  <span class="icon-play"></span>
                </div>
              </button>
            )}

            <button
              type="button"
              className="next"
              aria-label="Next"
              onClick={() => changeTrack(1)}
            >
              <div>
                <span class="icon-next"></span>
              </div>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Player;
