import { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

import Navbar from './components/Navbar/Navbar';
import Player from './components/Player';
import Header from './components/header';
import Formulaire from './components/Pages/Formulaire';
import HomePage from './components/Pages/HomePage';
import SuggestionPlaylist from './components/SuggestionPlaylist';
import DeezeflowPlaylists from './components/DeezeflowPlaylists';
import MyPlaylist from './components/MyPlaylist';
import ColorThemeContext from './Contexts/ColorThemeContext';
import StartQuestionnaireContext from './Contexts/StartQuestionnaire';
import PlayerContext from './Contexts/PlayerContext';
import './font.css';
import { useRef } from 'react';
function App() {
  const [ myMood, setMymood ] = useState();
	const [ step, setStep ] = useState(0);
	const [ savedMood, setSavedMood ] = useState('');
	const [ currentTracklist, setCurrentTracklist ] = useState();
	const [ myColorTheme, setMyColorTheme ] = useState();

	function resetStep() {
		setStep(0);
	}

  function setCurrentTrack(track) {
    currentTrackRef.current = track;
    _setCurrentTrack(track);
  }

	return (
    <PlayerContext.Provider value={{setCurrentTrack: setCurrentTrack}}>
		<StartQuestionnaireContext.Provider
			value={{
        myMood: myMood,
        setMymood: setMymood,
				step: step,
				setStep: setStep,
				resetStep: resetStep,
				savedMood: savedMood,
				setSavedMood: setSavedMood
			}}
		>
			<ColorThemeContext.Provider value={{ myColorTheme: myColorTheme, setMyColorTheme: setMyColorTheme }}>
				<Router>
					<div className="App">
						<Header setMyColorTheme={setMyColorTheme} />

						<Routes>
							<Route exact path="/" element={<HomePage />} />
							<Route path="/form" element={<Formulaire />} />
							<Route path="/suggests" element={<SuggestionPlaylist />} />
							<Route path="/deezeflow-playlists" element={<DeezeflowPlaylists />} />
							<Route
								path="/my-playlist/:id"
								element={
									<MyPlaylist
										onSendingForPlayer={(tracklist) => {
											setCurrentTracklist(tracklist);
										}}
									/>
								}
							/>
						</Routes>

						{currentTracklist && <Player className="player" tracks={currentTracklist} />}

						<Navbar />
					</div>
				</Router>
			</ColorThemeContext.Provider>
		</StartQuestionnaireContext.Provider>
    </PlayerContext.Provider>
	);
}

export default App;
