import { useState, useEffect } from 'react';

const MyPlaylist = (id) => {
  const [myPlaylist, setMyPlaylist] = useState([]);
  useEffect(() => {
    fetch(`http://localhost:3001/playlist/${id}`)
      .then((response) => response.json())
      .then((data) => setMyPlaylist(data.data));
  }, []);

  const [playerData, setPlayerData] = useState([]);
  useEffect(() => {
    myPlaylist.map((index) =>
      setPlayerData(
        playerData.push({
          bigPicture: index.album.cover_big,
          smallPicture: index.album.cover_small,
          title: index.title,
          artist: index.artist.name,
          track: index.preview,
        })
      )
    );
  }, [myPlaylist]);
  return (
    <div>
      <ul>
        {myPlaylist.map((index) => (
          <li>
            <img src={index.album.cover_small} alt="cover" /> {index.title}-
            {index.artist.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MyPlaylist;
