import { Link } from 'react-router-dom';
import './HomePage.css';
import StartQuestionnaireContext from '../../Contexts/StartQuestionnaire';
import ColorThemeContext from '../../Contexts/ColorThemeContext';
import { useContext } from 'react';
import { resetMood } from '../Questions/AlgoMood';

const HomePage = () => {
  const { resetStep } = useContext(StartQuestionnaireContext);
  const { myColorTheme } = useContext(ColorThemeContext);
  return (
    <div>
      <div className="divButton">
        <Link to="/form">
          <button 
          type="button" 
          className={`buttonMood ${myColorTheme ? `theme-${myColorTheme}-shadow` : 'theme-default-shadow'}`}
          onClick={() => {
            resetStep();
            resetMood();
          }}
          >
            <div>
            {' '}
            <h2> Dans quel mood es-tu?</h2>
            <p>
              {' '}
              <br /> Remplis notre questionnaire pour trouver la playlist
              parfaitement adaptée à ton humeur du moment!
            </p>{' '}
            </div>
          </button>
        </Link>{' '}
      </div>

      <div>
        <button type="button">
          <Link to="/deezeflow-playlists"> NOS PLAYLISTS </Link>
        </button>
      </div>
    </div>
  );
};

export default HomePage;
