import { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

import Navbar from './components/Navbar/Navbar';
import Player from './components/Player';
import Header from './components/header';
import Formulaire from './components/Pages/Formulaire';
import HomePage from './components/Pages/HomePage';
import SuggestionPlaylist from './components/SuggestionPlaylist';
import DeezeflowPlaylists from './components/DeezeflowPlaylists';
import MyPlaylist from './components/MyPlaylist';
import ColorThemeContext from './Contexts/ColorThemeContext';
import StartQuestionnaireContext from './Contexts/StartQuestionnaire';
import PlayerContext from './Contexts/PlayerContext';
import './font.css';

function App() {
  const [ myMood, setMymood ] = useState();
	const [ step, setStep ] = useState(0);
	const [ savedMood, setSavedMood ] = useState('');
	const [ currentTracklist, setCurrentTracklist ] = useState();
	const [ myColorTheme, setMyColorTheme ] = useState();

	function resetStep() {
		setStep(0);
	}



  // Référence vers l'élément DOM audio.
  // Permet d'appeler play, pause et de tracker le temps de la musique
  const currentTrackElm = useRef();

  // Hack pour pouvoir garder la musique courante à jour
  // dans les callbacks des listeners (addEventListener)
  const currentTrackRef = useRef(tracks[0]);

  // La musique en cours
  const [currentTrack, _setCurrentTrack] = useState(tracks[0]);

  // Le temps de la musique en cours
  const [currentTime, setCurrentTime] = useState(0);

  // Est-ce que la musique en cours défile ?
  const [isPlaying, setIsPlaying] = useState(true);

  // Change la vue du lecteur (petit ou grand)
  const [isDisplayOff, setIsDisplayOff] = useState(false);

  /**
   * Si la playlist change (tracks), on la joue automatiquement
   * et on lance la première musique.
   */
  useEffect(() => {
    setIsPlaying(true);
    setCurrentTrack(tracks[0]);
  }, [tracks])

  /**
   * Lorsque l'élément DOM audio est set, on l'initialise
   */
  useEffect(() => {
    // Si l'élément DOM n'est pas encore accessible, on sort
    if (!currentTrackElm.current) {
      return;
    }

    // Callback pour traquer le temps passé
    const onTimeUpdate = () => {
      setCurrentTime(currentTrackElm.current.currentTime);
    };

    // Callback pour savoir lorsque la musique est finie
    const onTimeEnded = () => {
      changeTrack(1);
    };

    // Ajout des listeners
    currentTrackElm.current.addEventListener('timeupdate', onTimeUpdate);
    currentTrackElm.current.addEventListener('ended', onTimeEnded);

    // Fonction exécutée lorsque le composant est démonté (détruit)
    return () => {
      // Si l'élément DOM à été initialisé, on retire ses event listener
      if (currentTrackElm.current) {
        currentTrackElm.current.removeEventListener('timeupdate', onTimeUpdate);
        currentTrackElm.current.removeEventListener('ended', onTimeEnded);
      }
    };
  }, [currentTrackElm.current]);

  /**
   * Lorsque la musique en cours change, on la charge (load)
   * puis si le lecteur était en cours de lecture, on lance play()
   */
  useEffect(() => {
    if (!currentTrack) {
      return;
    }

    currentTrackElm.current.load();

    if (isPlaying) {
      play();
    }
  }, [currentTrack]);

  /**
   * Cette fonction nous permet de garder currentTrackRef.current et
   * currentTrack synchronisés.
   */
  function setCurrentTrack(track) {
    currentTrackRef.current = track;
    _setCurrentTrack(track);
  }

  /**
   * Joue la musique courante
   */
  function play() {
    currentTrackElm.current.play();
    setIsPlaying(true);
  }

  /**
   * Met la musique courante en pause
   */
  function pause() {
    currentTrackElm.current.pause();
    setIsPlaying(false);
  }

  /**
   * Change de musique selon la direction :
   * 1 = avance
   * -1 = recule
   */
  function changeTrack(direction = 1) {
    // On récupère l'index de la musique suivante
    const nextTrackIndex = tracks.indexOf(currentTrackRef.current) + direction;

    // Si l'index de la prochaine musique n'est pas dans la range du tableau tracks,
    // on ne fait rien.
    if (nextTrackIndex < 0 || nextTrackIndex === tracks.length) {
      return;
    }

    // On reset le temps de la musique courante
    currentTrackElm.current.currentTime = 0;

    // On définit la prochaine musique
    setCurrentTrack(tracks[nextTrackIndex]);
  }

	return (
    <PlayerContext.Provider value={{
      setCurrentTrack: setCurrentTrack,
      currentTrackElm: currentTrackElm,
      currentTrackRef: currentTrackRef,
      currentTrack: currentTrack,
      _setCurrentTrack: _setCurrentTrack,
      currentTime:currentTime,
      setCurrentTime:setCurrentTime,
      isPlaying:isPlaying,
      setIsPlaying:setIsPlaying,
      isDisplayOff:isDisplayOff,
      setIsDisplayOff:setIsDisplayOff,
      onTimeUpdate:onTimeUpdate,
      onTimeEnded:onTimeEnded,
      play:play,
      pause:pause,
      changeTrack:changeTrack
    }}>
		<StartQuestionnaireContext.Provider
			value={{
        myMood: myMood,
        setMymood: setMymood,
				step: step,
				setStep: setStep,
				resetStep: resetStep,
				savedMood: savedMood,
				setSavedMood: setSavedMood
			}}
		>
			<ColorThemeContext.Provider value={{ myColorTheme: myColorTheme, setMyColorTheme: setMyColorTheme }}>
				<Router>
					<div className="App">
						<Header setMyColorTheme={setMyColorTheme} />

						<Routes>
							<Route exact path="/" element={<HomePage />} />
							<Route path="/form" element={<Formulaire />} />
							<Route path="/suggests" element={<SuggestionPlaylist />} />
							<Route path="/deezeflow-playlists" element={<DeezeflowPlaylists />} />
							<Route
								path="/my-playlist/:id"
								element={
									<MyPlaylist
										onSendingForPlayer={(tracklist) => {
											setCurrentTracklist(tracklist);
										}}
									/>
								}
							/>
						</Routes>

						{currentTracklist && <Player className="player" tracks={currentTracklist} />}

						<Navbar />
					</div>
				</Router>
			</ColorThemeContext.Provider>
		</StartQuestionnaireContext.Provider>
    </PlayerContext.Provider>
	);
}

export default App;
