import { useState, useEffect } from 'react';
import './MyPlaylist.css'
const MyPlaylist = ({myPlaylistId, onSendingForPlayer=()=>{}}) => {
  const [myPlaylist, setMyPlaylist] = useState([]);
  useEffect(() => {
    fetch(`http://localhost:3001/playlist/${myPlaylistId}`)
      .then((response) => response.json())
      .then((data) => setMyPlaylist(data.tracks.data));
      console.log(myPlaylist)
  }, []);

  const [playerData, setPlayerData] = useState([]);
  useEffect(() => {
    myPlaylist.map((index) =>
      setPlayerData(
        playerData.push({
          bigPicture: index.tracks.data.album.cover_big,
          smallPicture: index.tracks.data.album.cover_small,
          title: index.tracks.data.title,
          artist: index.tracks.data.artist.name,
          track: index.tracks.data.preview,
        })
      )
    );
  }, [myPlaylist]);
  return (
    <div>
			<img src= {data[picture_medium]}/>
      <button onClick={()=>{onSendingForPlayer(playerData)}}>PLAY</button>
      <ul>
        {myPlaylist.map((index) => (
          <li>
            <img src={index.album.cover_small} alt="cover" /> {index.title}-
            {index.artist.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MyPlaylist;
