import questionsData from '../Questions/QuestionsData';
import { addAnswer, resetMood, calculateScore } from '../Questions/AlgoMood';
import QuestionsComponent from '../Questions/QuestionsComponent';
import './Formulaire.css';
import { useState, useEffect } from 'react';
import { shuffle } from '../Questions/AlgoQuestion';
import { Link } from 'react-router-dom';

const Formulaire = ({ getItStarted ,startQuestions,onMoodChange = () => {} }) => {
	const [ step, setStep ] = useState(0);

	const [ myMood, setMymood ] = useState('');
	useEffect(
		() => {
			let score = calculateScore();
			setMymood(score);
		},
		[ step === questionsData.length ]
	);
	useEffect(
		() => {
			const elementScroll = document.querySelector('.question-screen');
			if (!elementScroll) {
				return;
			}
			setTimeout(() => (elementScroll.scrollTop = 0), 0);
		},
		[ step ]
	);

	function nextStep() {
		setStep(step + 1);
	}

	function resetStep() {
		setStep(0);
	}



	return (
		<div>
			{getItStarted && step !== questionsData.length ? (
				<QuestionsComponent
					questions={questionsData[step]}
					onAnswered={(answer) => {
						addAnswer(answer);
						nextStep();
					}}
				/>
			) : (
				getItStarted &&
				step === questionsData.length && (
					<div className="end-screen">
						<h1>Nous avons pu détecter un résultat</h1>
						<p className="mood">
							Apparemment tu es dans un mood de <span>{myMood}</span>
						</p>
						<button
							onClick={() => {
								resetMood();
								shuffle(questionsData, true);
								startQuestions();
								resetStep();
							}}
						>
							Rejouer
						</button>
						<Link to="/suggests">
							<button onClick={() => onMoodChange(myMood)}>Voir mes suggestions</button>
						</Link>
					</div>
				)
			)}
		</div>
	);
};

export default Formulaire;
