import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import "./MyPlaylist.css"
const MyPlaylist = ({ myPlaylistId, myColorTheme, onSendingForPlayer = () => {} }) => {
  const { id } = useParams();
  const [myPlaylist, setMyPlaylist] = useState([]);
  const [myPlaylistCover, setMyPlaylistCover] = useState('');

  useEffect(() => {
    fetch(`http://localhost:3001/playlist/${id || myPlaylistId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          return setMyPlaylist([]);
        }

        setMyPlaylistCover(data['picture_medium']);

        setMyPlaylist(
          data.tracks.data.map((track) => ({
            bigPicture: track.album.cover_big,
            smallPicture: track.album.cover_small,
            title: track.title,
            artist: track.artist.name,
            duration: 30,
            src: track.preview,
          }))
        );
      });
  }, []);

  return (
    <div className="playlist-container">
      <div className="playlist-cover">
        <img src={myPlaylistCover} alt="cover" />
      </div>

      <button
        className="buttonPlay"
        onClick={() => {
          onSendingForPlayer(myPlaylist);
        }}
      >
        PLAY
      </button>

      <div className="warpPlaylist">
        {myPlaylist.map((track) => (
          <div className="track-info-box active">
            <div className="track-info-text">
              <p className="title-track">{track.title}</p>
              <p className="singer">{track.artist}</p>
            </div>

            <button className={`play ${myColorTheme? `theme-${myColorTheme}-shadow`:"theme-default-shadow"}`}>
              <div>
                <span class="icon-play"></span>
              </div>
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyPlaylist;
