import { Link } from 'react-router-dom';
import './HomePage.css';
import { useContext } from 'react';
import ColorThemeContext from '../../Contexts/ColorThemeContext';

const HomePage = () => {
  const {myColorTheme} = useContext(ColorThemeContext)
  return (
    <div>
      <div className="divButton">
        <Link to="/form">
          <button type="button" className={`buttonMood ${myColorTheme ? `theme-${myColorTheme}-shadow` : 'theme-default-shadow'}`} >
            <div>
            {' '}
            <h2> Dans quel mood es-tu?</h2>
            <p>
              {' '}
              <br /> Remplis notre questionnaire pour trouver la playlist
              parfaitement adaptée à ton humeur du moment!
            </p>{' '}
            </div>
          </button>
        </Link>{' '}
      </div>

      <div>
        <button type="button">
          <Link to="/deezeflow-playlists"> NOS PLAYLISTS </Link>
        </button>
      </div>
    </div>
  );
};

export default HomePage;
